import React, { Component } from 'react';
import { Table, Pagination, Button, Input, Icon } from 'antd'
import { Link } from 'react-router-dom'
export default class shop extends Component {
    // constructor(props) {
    //     super(props)
    // }
    dataSource = [{
        key: '1',
        name: '胡彦斌',
        age: 32,
        address: '西湖区湖底公园1号'
    }, {
        key: '2',
        name: '胡彦祖',
        age: 42,
        address: '西湖区湖底公园1号'
    }];
    columns = [
        {
            align: 'center',
            title: '姓名',
            dataIndex: 'name',
            key: 'name',
        }, {
            align: 'center',
            title: '年龄',
            dataIndex: 'age',
            key: 'age',
        }, {
            align: 'center',
            title: '住址',
            dataIndex: 'address',
            key: 'address',
        }, {
            align: 'center',
            title: '操作',
            key: 'action',
            render: (text, record) => (
                <span>
                    <Link to=''>
                        绑定供应商
                    </Link>
                </span>
            ),
        }
    ];
    rowSelection = {
        onChange: (index, row) => { }
    }
    onChange = (pageNumber) => {
        console.log('Page: ', pageNumber);
    }
    render() {
        return (
            <div style={{ width: '100%' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '100%', height: 50 }}>
                    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <Button>商品同步</Button>
                        <Button style={{ margin: '0 10px' }}>类别同步</Button>
                        <Button>供应商同步</Button>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <Input prefix={<Icon type="search" style={{ color: '#b2b2b2' }} />} placeholder="搜索" />
                        <Button style={{ margin: '0 10px' }}>搜索</Button>
                        <Button>导出</Button>
                    </div>
                </div>
                <Table
                    bordered
                    loading={false}
                    pagination={false} //-- 不使用自带的分页器
                    rowSelection={this.rowSelection}
                    dataSource={this.dataSource}
                    columns={this.columns}
                    size='small'
                />
                <div style={{ width: '100%', display: 'flex', justifyContent: 'flex-end', alignItems: 'center', height: 60 }}>
                    <Pagination
                        showQuickJumper
                        showSizeChanger
                        defaultCurrent={1}
                        total={500}
                        onChange={this.onChange}
                    />
                </div>
                <div>
                    <div>
                        <span>请选择要导入的excel:</span>
                    </div>
                    <div></div>
                </div>
            </div >
        )
    }
}
