import { Layout, Icon, LocaleProvider } from 'antd';
import React, { Component } from 'react';
import MenuLayout from '../../components/layout/menu';
import HeadLayout from '../../components/layout/header';
import CrumbLayout from '../../components/layout/crumb';
import zhCN from 'antd/lib/locale-provider/zh_CN';
import SiderCustom from './SiderCustom.js'
const { Sider, Content } = Layout;
export default class Index extends Component {
  constructor(props) {
    super(props)
    this.state = {
      collapsed: false,
      openKeys: ['sub1']
    };
    this.openOldKeys = ['sub1']
    this.rootSubmenuKeys = ['sub1', 'sub2', 'sub4'];
  }
  componentDidMount() {
    const token = localStorage.getItem('token')
    //-- 判断登录
    if (!token) window.location.href = '/';
  }
  onOpenChange = (openKeys) => {
    const latestOpenKey = openKeys.find(key => this.state.openKeys.indexOf(key) === -1);
    if (this.rootSubmenuKeys.indexOf(latestOpenKey) === -1) {
      this.setState({ openKeys });
    } else {
      this.setState({
        openKeys: latestOpenKey ? [latestOpenKey] : [],
      });
      this.latestOpenKey = latestOpenKey;
    }
  }
  toggle = () => {
    if (this.state.collapsed) {
      this.setState({
        openKeys: this.openOldKeys
      }, () => {
        this.setState({
          collapsed: !this.state.collapsed,
        });
      });
    } else {
      this.openOldKeys = this.state.openKeys
      this.setState({
        openKeys: []
      }, () => {
        this.setState({
          collapsed: !this.state.collapsed,
        });
      });
    }
  }
  render() {
    return (
      <LocaleProvider locale={zhCN}>
        <Layout className="ant-layout-has-sider" style={{ height: "100%" }}>
          <div>123</div>
          <SiderCustom
            collapsed={this.state.collapsed}
            toggle={this.toggle}
          />
          <Layout>
            <div>头部</div>
            <Content>
              <div>123123131233</div>
            </Content>
          </Layout>
        </Layout>
      </LocaleProvider>
    );
  }
}
