import React, { Component } from 'react';
import { Table, Pagination, Button, Input, Icon, Divider } from 'antd'
import { Link } from 'react-router-dom'
export default class shop extends Component {
    dataSource = [{
        key: '1',
        name: '胡彦斌',
        age: 32,
        address: '西湖区湖底公园1号'
    }, {
        key: '2',
        name: '胡彦祖',
        age: 42,
        address: '西湖区湖底公园1号'
    }];
    columns = [
        {
            align: 'center',
            title: '姓名',
            dataIndex: 'name',
            key: 'name',
        }, {
            align: 'center',
            title: '年龄',
            dataIndex: 'age',
            key: 'age',
        }, {
            align: 'center',
            title: '住址',
            dataIndex: 'address',
            key: 'address',
        }, {
            align: 'center',
            title: '操作',
            key: 'action',
            render: (text, record) => (
                <span>
                    <Link to='bindSupplier'>
                        绑定供应商
                    </Link>
                </span>
            ),
        }
    ];
    rowSelection = {
        onChange: (index, row) => { }
    }
    render() {
        return (
            <div style={{ width: '100%' }}>
                <div style={{ width: '100%', height: 40, display: 'flex', justifyContent: 'flex-start', alignItems: 'center' }}>
                    <div style={{ width: '10%', textAlign: 'right' }}>商品编号： </div><div>1104010041</div>
                </div>
                <div style={{ width: '100%', height: 40, display: 'flex', justifyContent: 'flex-start', alignItems: 'center' }}>
                    <div style={{ width: '10%', textAlign: 'right' }}>商品名称： </div><div>喜德旺竹荪</div>
                </div>
                <div style={{ width: '100%', minHeight: 40, display: 'flex', justifyContent: 'flex-start', alignItems: 'center' }}>
                    <div style={{ width: '10%', textAlign: 'right' }}>供应商选择： </div>
                    <div style={{ width: '90%' }}>
                        <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: 10 }}>
                            <Input size="small" prefix={<Icon type="search" style={{ color: '#b2b2b2' }} />} placeholder="搜索" style={{ width: 200 }} />
                            <Button style={{ margin: '0 10px' }} size='small'>搜索</Button>
                            <Button size='small'>选择</Button>
                        </div>
                        <div style={{ maxHeight: 200, overflow: 'auto' }}>
                            <Table
                                bordered
                                loading={false}
                                pagination={false} //-- 不使用自带的分页器
                                rowSelection={this.rowSelection}
                                dataSource={this.dataSource}
                                columns={this.columns}
                                size='small'
                            />
                        </div>
                        <div style={{ maxHeight: 200, overflow: 'auto' }}>
                            <div>
                                供应商编号	供应商名称	是否默认供应商	商品状态	合同类型	正常进价	是否可退货	操作
                            </div>
                            <div>

                            </div>
                        </div>
                    </div>
                </div>
            </div >
        )
    }
}
