// -- 绑定供应商
import React, { Component } from 'react';
import { Table, Form, Select, Button, Input, Icon } from 'antd'
const Option = Select.Option;
const { TextArea } = Input;
const FormItem = Form.Item;
class goodsTypeAdd extends Component {
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (err) return;
            console.log(values)
        });
    }
    render() {
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: { span: 12 },
                sm: { span: 2 },
            },
            wrapperCol: {
                xs: { span: 12 },
                sm: { span: 22 },
            },
        };
        return (
            <Form onSubmit={this.handleSubmit} style={{ width: '100%' }}>
                <FormItem
                    {...formItemLayout}
                    label="父类型编号:"
                >
                    {getFieldDecorator('parent_code', {
                        rules: [{
                            required: true, message: '请选择父类型！',
                        }],
                    })(
                        <Select placeholder='请选择' style={{ width: '25%' }}>
                            <Option value="0">代销</Option>
                            <Option value="1">经营</Option>
                            <Option value="2">联营</Option>
                        </Select>
                    )}
                </FormItem>
                <FormItem
                    label="产品类型编号:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pro_type_code', {
                        rules: [{
                            required: true, message: '请输入产品类型编号！',
                        }],
                    })(
                        <Input style={{ width: '25%' }} />
                    )}
                </FormItem>
                <FormItem
                    label="产品类型名称:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pro_type_name', {
                        rules: [{
                            required: true, message: '请输入产品类型名称！',
                        }],
                    })(
                        <Input style={{ width: '25%' }} />
                    )}
                </FormItem>
                <FormItem
                    label="产品类型备注:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('remark', {
                        rules: [],
                    })(
                        <TextArea style={{ width: '25%' }} rows={4} />
                    )}
                </FormItem>
                <FormItem
                    label="采购人员姓名:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('user_name', {
                        rules: [],
                    })(
                        <Input style={{ maxWidth: 200 }} />
                    )}
                </FormItem>
                <FormItem wrapperCol={{ span: 8, offset: 2 }}>
                    <Button onClick={this.check} icon='save'>
                        提交
                    </Button>
                    <Button onClick={this.check} style={{ marginLeft: 10 }} icon='rollback'>
                        返回
                    </Button>
                </FormItem>
            </Form>
        )
    }
}
const goodsTypeAdds = Form.create()(goodsTypeAdd)
export default goodsTypeAdds
