import React, { Component } from 'react';
import { Table, Divider } from 'antd'
export default class shop extends Component {
    // constructor(props) {
    //     super(props)
    // }
    dataSource = [{
        key: '1',
        name: '胡彦斌',
        age: 32,
        address: '西湖区湖底公园1号'
    }, {
        key: '2',
        name: '胡彦祖',
        age: 42,
        address: '西湖区湖底公园1号'
    }];
    columns = [
        {
            align: 'center',
            title: '姓名',
            dataIndex: 'name',
            key: 'name',
        }, {
            align: 'center',
            title: '年龄',
            dataIndex: 'age',
            key: 'age',
        }, {
            align: 'center',
            title: '住址',
            dataIndex: 'address',
            key: 'address',
        }, {
            align: 'center',
            title: '操作',
            key: 'action',
            render: (text, record) => (
                <span>
                    <a href="javascript:;">
                        绑定供应商
                </a>
                </span>
            ),
        }
    ];
    rowSelection = {
        onChange: (index, row) => { }
    }
    render() {
        return (
            <div>
                <Table
                    // bordered  //-- 是否展示外边框
                    pagination={false} //-- 不使用自带的分页器
                    rowSelection={this.rowSelection}
                    dataSource={this.dataSource}
                    columns={this.columns}
                    style={{ width: '100%' }}
                />
            </div>
        )
    }
}
