let CryptoJS = require("crypto-js");
export const BASEURL = 'http://10.20.10.81:8877/';
var reqUrl = {
    registUrl: BASEURL + '/Api/Register/addUser',
    getGoodsCategoryList: BASEURL + '/api/ProductType/index',
}
function isArray(obj) {
    return obj instanceof Array;
}

/*** 对请求数据进行加密*/
function serviceRequestEncryption(bodyData) {
    let preKey = '87749CECEA24B1C314CC27CF7952EBC3'; //Md5加密（32位大写）
    let tempBodyData = [];
    if (isObject(bodyData)) {
        tempBodyData = bodyData;
    }
    tempBodyData['isAjax'] = '1';
    let objKeys = Object.keys(tempBodyData);
    objKeys.sort(); //排序

    let signStr = '';
    objKeys.forEach(item => {
        signStr = signStr + tempBodyData[item];
    });
    signStr = signStr + preKey;

    let md51 = CryptoJS.MD5(signStr).toString();
    let md51Super = md51.toUpperCase();

    md51Super = md51Super.substring(2, 18);
    let md52 = CryptoJS.MD5(md51Super).toString();

    tempBodyData['sign'] = md52.toUpperCase();

    return tempBodyData;
}
export function NetWork_Post(net_api, bodyData, callback, netOptions, securityParam) {
    // 加密
    bodyData = serviceRequestEncryption(bodyData);
    let opt_headers, opt_error;
    if (typeof netOptions === 'object') {
        opt_headers = netOptions['headers'];
        opt_error = netOptions['error'];
    };

    let post_header = opt_headers ? opt_headers : {
        'Accept': 'application/json',
        'Content-Type': 'application/x-www-form-urlencoded',
    };

    let post_error = opt_error ? opt_error : {
        status: '1004',
        msg: '网络延时，请稍后重试！',
    };
    console.log(formData.getAll())
    let url = reqUrl[net_api];
    let fetchOptions = {
        method: 'POST',
        headers: post_header,
        body: formData
    }
    fetch(url, fetchOptions)
        .then((response) => response.text())
        .then((responseText) => {
            let responseData = JSON.parse(responseText);
            callback(responseData);//回调
        })
        .catch(error => {
            console.log(error);
            callback(post_error);
        });

}

export function NetWork_Get(net_api, callback, netOptions) {

    let url = reqUrl[net_api];
    let opt_error;

    if (typeof netOptions === 'object') {
        opt_error = netOptions['error'];
    };

    let get_error = opt_error ? opt_error : {
        status: '-1',
        msg: '请求失败',
    };

    fetch(url, {
        method: 'GET',
        credentials: 'include'
    })
        .then((response) => response.text())
        .then((responseText) => {
            let responseData = JSON.parse(responseText);
            callback(responseData);
        })
        .catch((error) => {
            callback(get_error);
        });
};
