import { Menu, Icon, Layout } from 'antd';
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
const SubMenu = Menu.SubMenu;
const { Sider } = Layout;
export default class MenuLayout extends Component {
    constructor(props) {
        super(props)
        this.state = {
            collapsed: false,
            openKeys: ['sub1']
        };
        this.openOldKeys = ['sub1']
        this.rootSubmenuKeys = ['sub1', 'sub2', 'sub4'];
    }
    onOpenChange = (openKeys) => {
        const latestOpenKey = openKeys.find(key => this.state.openKeys.indexOf(key) === -1);
        if (this.rootSubmenuKeys.indexOf(latestOpenKey) === -1) {
            this.setState({ openKeys });
        } else {
            this.setState({
                openKeys: latestOpenKey ? [latestOpenKey] : [],
            });
            this.latestOpenKey = latestOpenKey;
        }
    }
    toggle = () => {
        if (this.state.collapsed) {
            this.setState({
                openKeys: this.openOldKeys
            }, () => {
                this.setState({
                    collapsed: !this.state.collapsed,
                });
            });
        } else {
            this.openOldKeys = this.state.openKeys
            this.setState({
                openKeys: []
            }, () => {
                this.setState({
                    collapsed: !this.state.collapsed,
                });
            });
        }
    }
    render() {
        return (
            <Sider>
                trigger={null}
                collapsible
            collapsed={this.state.collapsed}
                >
            <Menu
                    mode="inline"
                    theme="light"
                    defaultSelectedKeys={['1']}
                    openKeys={this.props.openKeys}
                    onOpenChange={this.props.onOpenChange}
                >
                    <SubMenu key="sub1" title={<span><Icon type="mail" /><span>Navigation One</span></span>}>
                        {/* <Menu.Item key="1"><Link to='home/home'>商品管理</Link></Menu.Item> */}
                        <Menu.Item key="3">Option 3</Menu.Item>
                        <Menu.Item key="2"><Link to='user'>用户管理</Link></Menu.Item>
                        <Menu.Item key="4">Option 4</Menu.Item>
                    </SubMenu>
                    <SubMenu key="sub2" title={<span><Icon type="appstore" /><span>Navigation Two</span></span>}>
                        <Menu.Item key="5">Option 5</Menu.Item>
                        <Menu.Item key="6">Option 6</Menu.Item>
                        <SubMenu key="sub3" title="Submenu">
                            <Menu.Item key="7">Option 7</Menu.Item>
                            <Menu.Item key="8">Option 8</Menu.Item>
                        </SubMenu>
                    </SubMenu>
                    <SubMenu key="sub4" title={<span><Icon type="setting" /><span>Navigation Three</span></span>}>
                        <Menu.Item key="9">Option 9</Menu.Item>
                        <Menu.Item key="10">Option 10</Menu.Item>
                        <Menu.Item key="11">Option 11</Menu.Item>
                        <Menu.Item key="12">Option 12</Menu.Item>
                    </SubMenu>
                </Menu>
            </Sider>
        );
    }
}
