// -- 添加仓库
import React, { Component } from 'react';
import { Form, Select, Button, Input, message } from 'antd'
import { NetWork_Post } from '../../network/netUtils'
const Option = Select.Option;
const { TextArea } = Input;
const FormItem = Form.Item;
const areaList = require('../../components/area.json')
class warehouseAdd extends Component {
    constructor(props) {
        super(props)
        this.state = {
            provinceIndex: null,
            cityIndex: null,
            areaIndex: null
        }
    }
    componentDidMount() {
        //-- 获取顶级地址
        this._getProvinceList()
    }
    _getProvinceList = () => {

    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (err) return;
            values.province = areaList[this.state.provinceIndex].name
            values.city = areaList[this.state.provinceIndex].city[this.state.cityIndex].name
            values.area = areaList[this.state.provinceIndex].city[this.state.cityIndex].area[this.state.areaIndex]
            NetWork_Post('warehouseAdd', values, (response) => {
                const { status, msg } = response
                if (status === '0000') {
                    message.success(msg)
                    return this.props.history.goBack();
                } else {
                    if (status === '1003') return this.props.history.push('/');
                    message.error(msg)
                }
            });
        });
    }
    selectProvince = (item) => {
        this.setState({
            provinceIndex: item,
            cityIndex: null,
            areaIndex: null
        })
        this.props.form.setFieldsValue({
            province: item,
            city: null,
            area: null
        })
    }
    selectCity = (item) => {
        this.setState({
            cityIndex: item,
            areaIndex: null
        })
        this.props.form.setFieldsValue({
            city: item,
            area: null
        })
    }
    selectArea = (item) => {
        this.setState({
            areaIndex: item
        })
    }
    render() {
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: { span: 12 },
                sm: { span: 3 },
            },
            wrapperCol: {
                xs: { span: 12 },
                sm: { span: 21 },
            },
        };
        return (
            <Form onSubmit={this.handleSubmit} style={{ width: '100%' }}>
                <FormItem
                    label="仓库编号:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('warehouse_num', {
                        rules: [{
                            required: true, message: '请输入仓库编号！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="仓库名称:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('warehouse_name', {
                        rules: [{
                            required: true, message: '请输入仓库名称！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="负责人姓名:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pic_name', {
                        rules: [{
                            required: true, message: '请输入负责人姓名！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="负责人电话:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pic_tel', {
                        rules: [],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="负责人手机:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pic_mobile', {
                        rules: [{
                            required: true, message: '请输入负责人手机！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="负责人传真:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pic_fax', {
                        rules: [],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="负责人qq:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pic_qq', {
                        rules: [],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="负责人微信:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pic_wechat', {
                        rules: [],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="负责人邮箱:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pic_email', {
                        rules: [],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    {...formItemLayout}
                    label="仓库所在省:"
                >
                    {getFieldDecorator('province', {
                        rules: [{
                            required: true, message: '请选择仓库所在省！',
                        }],
                    })(
                        <Select placeholder='请选择' style={{ maxWidth: 300 }} onChange={this.selectProvince} >
                            {
                                areaList ? areaList.map((item, index) => (
                                    <Option key={index} value={index}>{item.name}</Option>
                                )) : null
                            }
                        </Select>
                    )}
                </FormItem>
                <FormItem
                    {...formItemLayout}
                    label="仓库所在市:"
                >
                    {getFieldDecorator('city', {
                        rules: [{
                            required: true, message: '请选择仓库所在市！',
                        }],
                    })(
                        <Select placeholder='请选择' style={{ maxWidth: 300 }} onChange={this.selectCity}>
                            {
                                areaList[this.state.provinceIndex] ? areaList[this.state.provinceIndex].city.map((item, index) => (
                                    <Option key={index} value={index} >{item.name}</Option>
                                )) : null
                            }
                        </Select>
                    )}
                </FormItem>
                <FormItem
                    {...formItemLayout}
                    label="仓库所在区:"
                >
                    {getFieldDecorator('area', {
                        rules: [{
                            required: true, message: '请选择仓库所在区！',
                        }],
                    })(
                        <Select placeholder='请选择' style={{ maxWidth: 300 }} onChange={this.selectArea}>
                            {
                                areaList[this.state.provinceIndex] && areaList[this.state.provinceIndex].city[this.state.cityIndex] ? areaList[this.state.provinceIndex].city[this.state.cityIndex].area.map((item, index) => (
                                    <Option key={index} value={index}>{item}</Option>
                                )) : null
                            }
                        </Select>
                    )}
                </FormItem>
                <FormItem
                    label="具体地址:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('address', {
                        rules: [{
                            required: true, message: '请输入具体地址！',
                        }],
                    })(
                        <TextArea style={{ maxWidth: 300 }} rows={3} />
                    )}
                </FormItem>
                <FormItem
                    label="接收日期:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('receiving_date', {
                        rules: [],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="备注:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('remark', {
                        rules: [],
                    })(
                        <TextArea style={{ maxWidth: 300 }} rows={3} />
                    )}
                </FormItem>
                <FormItem wrapperCol={{ span: 8, offset: 3 }}>
                    <div style={{ maxWidth: 300 }}>
                        <Button icon='save' htmlType="submit" style={{ float: "left" }}>
                            提交
                        </Button>
                        <Button onClick={() => { this.props.history.goBack() }} style={{ marginLeft: 10, float: "right" }} icon='rollback'>
                            返回
                        </Button>
                    </div>
                </FormItem>
            </Form>
        )
    }
}
export default Form.create()(warehouseAdd)
