// -- 商品管理
import React, { Component } from 'react';
import { Table, Pagination, Button, Input, Icon, Divider, message } from 'antd'
import { Link } from 'react-router-dom'
import { NetWork_File, NetWork_Post } from '../../network/netUtils'
export default class goods extends Component {
    constructor(props) {
        super(props)
        this.state = {
            data: [],
            page: 1,
            limit: 10,
            search_key: '',
            selectedRowKeys: [],
            fileList1: [],
            fileList2: [],
            uploading1: false,
            uploading2: false,
        }
    }

    componentDidMount() {
        //-- 获取商品类型列表
        this._getGoodsTypeList()
        //-- 获取模版下载地址
        this._getProTypeDownload()
    }
    _getProTypeDownload = () => {
        const formData = {
        }
        NetWork_Post('proTypeDownload', formData, (response) => {
            const { status, data, msg } = response
            if (status === '0000') {
                this.setState({
                    proTypeDownload: data,
                })
            } else {
                if (status === '1003') return this.props.history.push('/');
                message.error(msg)
            }
        });
    }
    _getGoodsTypeList = () => {
        const formData = {
            search_key: this.state.search_key,
            limit: this.state.limit,
            page: this.state.page
        }
        NetWork_Post('getGoodsTypeList', formData, (response) => {
            const { status, data, msg } = response
            if (status === '0000') {
                this.setState({
                    data: data.goodTypeList,
                    total: data.goodTypeCount
                })
            } else {
                if (status === '1003') return this.props.history.push('/');
                message.error(msg)
            }
        });
    }

    columns = [
        {
            align: 'center',
            title: '姓名',
            dataIndex: 'name',
            key: 'name',
        }, {
            align: 'center',
            title: '年龄',
            dataIndex: 'age',
            key: 'age',
        }, {
            align: 'center',
            title: '住址',
            dataIndex: 'address',
            key: 'address',
        }, {
            align: 'center',
            title: '操作',
            key: 'action',
            render: (text, record) => (
                <span>
                    <Link to='bindSupplier'>
                        绑定供应商
                    </Link>
                </span>
            ),
        }
    ];
    rowSelection = {
        onChange: (index, row) => { }
    }
    onChange = (pageNumber) => {
        console.log('Page: ', pageNumber);
    }
    render() {
        return (
            <div style={{ width: '100%' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '100%', height: 50 }}>
                    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <Button icon="cloud-download">商品同步</Button>
                        <Button style={{ margin: '0 10px' }} icon="cloud-download">类别同步</Button>
                        <Button icon="cloud-download">供应商同步</Button>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <Input prefix={<Icon type="search" style={{ color: '#b2b2b2' }} />} placeholder="搜索" />
                        <Button style={{ margin: '0 10px' }} icon='search'>搜索</Button>
                        <Button icon='file-done'>导出</Button>
                    </div>
                </div>
                <Table
                    bordered
                    loading={false}
                    pagination={false} //-- 不使用自带的分页器
                    rowSelection={this.rowSelection}
                    dataSource={this.dataSource}
                    columns={this.columns}
                    size='small'
                />
                <div style={{ width: '100%', display: 'flex', justifyContent: 'flex-end', alignItems: 'center', height: 60 }}>
                    <Pagination
                        showQuickJumper
                        showSizeChanger
                        defaultCurrent={1}
                        total={500}
                        onChange={this.onChange}
                    />
                </div>
                <div style={{ width: '100%', borderTop: '1px solid #EFEFEF' }}>
                    <div style={{ margin: '10px 0' }}>
                        <span>请选择要导入的Excel: <Button size='small'>选取文件</Button></span>
                        <Divider type="vertical" />
                        <Button size='small'>商品信息导入</Button>
                        <Divider type="vertical" />
                        <Button size='small' icon="download">商品信息导入模板下载</Button>
                    </div>
                    <div>
                        <span>请选择要导入的Excel: <Button size='small'>选取文件</Button></span>
                        <Divider type="vertical" />
                        <Button size='small'>商品供应商关系信息导入</Button>
                        <Divider type="vertical" />
                        <Button size='small' icon="download">商品供应商关系信息模板下载</Button>
                    </div>
                </div>
            </div >
        )
    }
}
