// -- 添加大客户需求订单
import React, { Component } from 'react';
import { Form, Select, Button, Input, message, DatePicker, Radio } from 'antd'
import { NetWork_Post } from '../../network/netUtils'
import { dateTransform } from '../../utils/utils_date';
const Option = Select.Option;
const { TextArea } = Input;
const FormItem = Form.Item;
const RadioGroup = Radio.Group;
class vendorAdd extends Component {
    componentDidMount() {
        this.props.form.setFieldsValue({
            order_num: 'Y' + dateTransform(new Date(), 'yyyyMMddhhmmss')
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (err) return;
            console.log(values); return;
            NetWork_Post('vendorAdd', values, (response) => {
                const { status, msg } = response
                if (status === '0000') {
                    message.success(msg)
                    return this.props.history.goBack();
                } else {
                    if (status === '1003') return this.props.history.push('/');
                    message.error(msg)
                }
            });
        });
    }
    _onChangePicker = (date, dateString) => {
        console.log(date, dateString);
    }
    _onChangeRadio = () => {

    }
    render() {
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: { span: 12 },
                sm: { span: 3 },
            },
            wrapperCol: {
                xs: { span: 12 },
                sm: { span: 21 },
            },
        };
        return (
            <Form onSubmit={this.handleSubmit} style={{ width: '100%' }}>
                <FormItem
                    label="大客户需求说明:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('remark', {
                        rules: [],
                    })(
                        <TextArea style={{ maxWidth: 300 }} rows={5} />
                    )}
                </FormItem>
                <FormItem
                    label="订单号:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('order_num', {
                        rules: [{
                            required: true, message: '请输入厂商编号！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} disabled />
                    )}
                </FormItem>
                <FormItem
                    label="客户名称:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('member_name', {
                        rules: [{
                            required: true, message: '请输入客户名称！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="过期时间:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('order_end_date', {
                        rules: [{
                            required: true, message: '请选择过期时间！',
                        }],
                    })(
                        <DatePicker onChange={this._onChangePicker} />
                    )}
                </FormItem>
                <FormItem
                    {...formItemLayout}
                    label="订单类型:"
                >
                    {getFieldDecorator('t_type', {
                        rules: [{
                            required: true, message: '请选择订单类型！',
                        }],
                    })(
                        <RadioGroup>
                            <Radio value={1}>A</Radio>
                            <Radio value={2}>B</Radio>
                            <Radio value={3}>C</Radio>
                            <Radio value={4}>D</Radio>
                        </RadioGroup>
                    )}
                </FormItem>

                <FormItem wrapperCol={{ span: 8, offset: 3 }}>
                    <div style={{ maxWidth: 300 }}>
                        <Button icon='save' htmlType="submit" style={{ float: "left" }}>
                            提交
                        </Button>
                        <Button onClick={() => { this.props.history.goBack() }} style={{ marginLeft: 10, float: "right" }} icon='rollback'>
                            返回
                        </Button>
                    </div>
                </FormItem>
            </Form>
        )
    }
}
export default Form.create()(vendorAdd)
