import React, { Component } from "react";
import { Layout, Menu } from "antd";
import { Link } from "react-router-dom";

const SubMenu = Menu.SubMenu;
const { Sider } = Layout;

export default class SiderCustom extends Component {
  constructor(props) {
    super(props);
    const { collapsed } = props;
    this.state = {
      collapsed: collapsed,
      firstHide: true, //第一次先隐藏暴露的子菜单
      selectedKey: "", //选择的路径
      openKey: "", //打开的路径（选择的上一层）
      menus: []
    };
  }

  componentDidMount() {

    const menus = localStorage.getItem("menus");
    let child = this.deepClone(JSON.parse(menus)[0].child);
    this.setState({ menus: child });

    this.setMenuOpen(this.props);
  }

  // 组件销毁前移除事件监听

  componentWillReceiveProps(nextProps) {
    this.onCollapse(nextProps.collapsed);
    this.setMenuOpen(nextProps);
  }

  setMenuOpen = props => {
    const { path } = props;
    this.setState({
      openKey: path.substr(0, path.lastIndexOf("/")),
      selectedKey: path
    });
  };
  onCollapse = collapsed => {
    this.setState({
      collapsed,
      firstHide: collapsed
    });
  };
  menuClick = e => {
    this.setState({
      selectedKey: e.key
    });
  };
  openMenu = v => {
    this.setState({
      openKey: v[v.length - 1],
      firstHide: false
    });
  };
  afterPic = menuName => {
    let a = "";
    switch (menuName) {
      case "电话质检":
        a = "";
        break;
      case "在线质检":
        a = "";
        break;
      case "敏感词设置":
        a = "";
        break;
      case "质检任务分配":
        a = "";
        break;
      case "质检件跟进":
        a = "";
        break;
      case "质检报表":
        a = "";
        break;
      case "车险质检报表":
        a = "";
        break;
      case "质检评分模板设置":
        a = "";
        break;
      default:
        break;
    }
    return a;
  };

  Pic = menuName => {
    let a = "";
    switch (menuName) {
      case "电话质检":
        a = one;
        break;
      case "在线质检":
        a = two;
        break;
      case "敏感词设置":
        a = three;
        break;
      case "质检任务分配":
        a = five;
        break;
      case "质检件跟进":
        a = follow;
        break;
      case "质检报表":
        a = state;
        break;
      case "车险质检报表":
        a = state;
        break;
      case "质检评分模板设置":
        a = template;
        break;
      default:
        break;
    }
    return a;
  };
  getType = obj => {
    //tostring会返回对应不同的标签的构造函数
    let toString = Object.prototype.toString;
    let map = {
      "[object Boolean]": "boolean",
      "[object Number]": "number",
      "[object String]": "string",
      "[object Function]": "function",
      "[object Array]": "array",
      "[object Date]": "date",
      "[object RegExp]": "regExp",
      "[object Undefined]": "undefined",
      "[object Null]": "null",
      "[object Object]": "object"
    };
    return map[toString.call(obj)];
  };
  deepClone = data => {
    let type = this.getType(data);
    let obj;
    if (type === "array") {
      obj = [];
    } else if (type === "object") {
      obj = {};
    } else {
      //不再具有下一层次
      return data;
    }
    if (type === "array") {
      for (let i = 0, len = data.length; i < len; i++) {
        obj.push(this.deepClone(data[i]));
      }
    } else if (type === "object") {
      for (let key in data) {
        obj[key] = this.deepClone(data[key]);
      }
    }
    return obj;
  };

  render() {
    const { collapsed, firstHide, openKey, selectedKey } = this.state;
    return (
      <Sider
        trigger={null}
        collapsed={collapsed}
        style={{ background: "#ffffff" }}
      >
        <div className="logo">
          <span>
            {collapsed ? "" : "客服质检中心"}
            <img
              src={'../../components/layout/src/nomatch.jpg'}
              alt=""
              style={{
                width: "20px",
                height: "18px",
                marginLeft: 5,
                cursor: "pointer"
              }}
              onClick={this.props.toggle}
            />
          </span>
        </div>
        <Menu
          theme="light"
          style={{ borderColor: "#ffffff" }}
          selectedKeys={[selectedKey]}
          onClick={this.menuClick}
          onSelect={this.onSelect}
          onOpenChange={this.openMenu}
          openKeys={firstHide ? null : [openKey]}
          mode="inline"
        >
          {this.state.menus.length > 0 &&
            this.state.menus.map((item, index) => {
              if (item.child.length > 0) {
                return (
                  <SubMenu
                    key={
                      item.menuName === "电话质检"
                        ? "/encore/app/tel"
                        : item.link
                    }
                    title={
                      <span>
                        <img
                          src={'../../components/layout/src/nomatch.jpg'}
                          alt=""
                          style={{
                            width: "20px",
                            height: "20px",
                            display: "inline"
                          }}
                        />
                        <span
                          style={{
                            marginLeft: this.state.collapsed ? "20px" : "12px"
                          }}
                        >
                          {item.menuName}
                        </span>
                      </span>
                    }
                  >
                    {item.child.map((ite, ind) => {
                      return (
                        <Menu.Item
                          key={
                            ite.menuName === "录音质检"
                              ? "/encore/app/tel/quality"
                              : ite.menuName === "录音复检"
                                ? "/encore/app/tel/reexamination"
                                : ite.link
                          }
                        >
                          <Link
                            to={
                              ite.menuName === "录音质检"
                                ? "/encore/app/tel/quality"
                                : ite.menuName === "录音复检"
                                  ? "/encore/app/tel/reexamination"
                                  : ite.link
                            }
                          >
                            {ite.menuName}
                          </Link>
                        </Menu.Item>
                      );
                    })}
                  </SubMenu>
                );
              } else {
                return (
                  <Menu.Item key={item.link}>
                    <Link to={item.link}>
                      <img
                        src={'../../components/layout/src/nomatch.jpg'}
                        alt=""
                        style={{
                          width: "20px",
                          height: "20px",
                          display: "inline"
                        }}
                      />
                      <span
                        style={{
                          marginLeft: this.state.collapsed ? "20px" : "12px"
                        }}
                      >
                        {item.menuName}
                      </span>
                    </Link>
                  </Menu.Item>
                );
              }
            })}

          {/*<SubMenu key={'/encore/app/statement'} title={*/}
          {/*<span>*/}
          {/*<img src={state} alt="" style={{ width: '20px', height: '20px', display: 'inline' }}/>*/}
          {/*<span style={{ marginLeft: this.state.collapsed ? "20px" : "12px" }}>质检报表</span>*/}
          {/*</span>}>*/}
          {/*<Menu.Item key={'/encore/app/statement/qcWork'}>*/}
          {/*<Link to={'/encore/app/statement/qcWork'}>自有人员工作量报表</Link>*/}
          {/*</Menu.Item>*/}
          {/*<Menu.Item key={'/encore/app/statement/qcDetailReport'}>*/}
          {/*<Link to={'/encore/app/statement/qcDetailReport'}>质检明细报表</Link>*/}
          {/*</Menu.Item>*/}
          {/*<Menu.Item key={'/encore/app/statement/qcDetailAnalysis'}>*/}
          {/*<Link to={'/encore/app/statement/qcDetailAnalysis'}>质检明细分析报表</Link>*/}
          {/*</Menu.Item>*/}
          {/*<Menu.Item key={'/encore/app/statement/qcAnalysis'}>*/}
          {/*<Link to={'/encore/app/statement/qcAnalysis'}>质检分析报表</Link>*/}
          {/*</Menu.Item>*/}
          {/*</SubMenu>*/}
        </Menu>
      </Sider>
    );
  }
}
