import React, { Component } from 'react';
import { Table, Pagination, Button, Input, Icon, Divider } from 'antd'
import { Link } from 'react-router-dom'
export default class shop extends Component {
    dataSource = [{
        key: '1',
        name: '胡彦斌',
        age: 32,
        address: '西湖区湖底公园1号'
    }, {
        key: '2',
        name: '胡彦祖',
        age: 42,
        address: '西湖区湖底公园1号'
    }];
    columns = [
        {
            align: 'center',
            title: '姓名',
            dataIndex: 'name',
            key: 'name',
        }, {
            align: 'center',
            title: '年龄',
            dataIndex: 'age',
            key: 'age',
        }, {
            align: 'center',
            title: '住址',
            dataIndex: 'address',
            key: 'address',
        }, {
            align: 'center',
            title: '操作',
            key: 'action',
            render: (text, record) => (
                <span>
                    <Link to='bindSupplier'>
                        绑定供应商
                    </Link>
                </span>
            ),
        }
    ];
    rowSelection = {
        onChange: (index, row) => { }
    }
    onChange = (pageNumber) => {
        console.log('Page: ', pageNumber);
    }
    render() {
        return (
            <div style={{ width: '100%' }}>
                <Table
                    bordered
                    loading={false}
                    pagination={false} //-- 不使用自带的分页器
                    rowSelection={this.rowSelection}
                    dataSource={this.dataSource}
                    columns={this.columns}
                    size='small'
                />
                <div style={{ width: '100%', display: 'flex', justifyContent: 'flex-end', alignItems: 'center', height: 60 }}>
                    <Pagination
                        showQuickJumper
                        showSizeChanger
                        defaultCurrent={1}
                        total={500}
                        onChange={this.onChange}
                    />
                </div>
                <div style={{ width: '100%', borderTop: '1px solid #EFEFEF' }}>
                    <div style={{ margin: '10px 0' }}>
                        <span>请选择要导入的Excel: <Button size='small'>选取文件</Button></span>
                        <Divider type="vertical" />
                        <Button size='small'>商品信息导入</Button>
                        <Divider type="vertical" />
                        <Button size='small'>商品信息导入模板下载</Button>
                    </div>
                    <div>
                        <span>请选择要导入的Excel: <Button size='small'>选取文件</Button></span>
                        <Divider type="vertical" />
                        <Button size='small'>商品供应商关系信息导入</Button>
                        <Divider type="vertical" />
                        <Button size='small'>商品供应商关系信息模板下载</Button>
                    </div>
                </div>
            </div >
        )
    }
}
