let CryptoJS = require("crypto-js");
export const BASEURL = 'http://10.20.10.81:8877/';
var reqUrl = {
    registUrl: BASEURL + '/Api/Register/addUser',
    getGoodsCategoryList: BASEURL + '/api/ProductType/index',
}
function isArray(obj) {
    return obj instanceof Array;
}

export function NetWork_Post(net_api, bodyData, callback, netOptions, securityParam) {

    if (isArray(securityParam)) {
        var formData = new FormData();
        let param = 'param';
        for (var i = 0; i < securityParam.length; i++) {
            let keyString = securityParam[i];
            if (bodyData[keyString]) {
                formData.append(keyString, bodyData[keyString])
                param = param + bodyData[keyString];
            }
        }

        let md51 = CryptoJS.MD5(param).toString();
        formData.append("keyStr", md51);
    }

    console.log(formData)
    let opt_headers, opt_error;
    if (typeof netOptions === 'object') {
        opt_headers = netOptions['headers'];
        opt_error = netOptions['error'];
    };

    let post_header = opt_headers ? opt_headers : {
        'Accept': 'application/json',
        'Content-Type': 'application/x-www-form-urlencoded',
    };

    let post_error = opt_error ? opt_error : {
        status: '1004',
        msg: '网络延时，请稍后重试！',
    };

    let url = reqUrl[net_api];
    let fetchOptions = {
        method: 'POST',
        headers: post_header,
        body: JSON.stringify(bodyData),
    }

    fetch(url, fetchOptions)
        .then((response) => response.text())
        .then((responseText) => {
            let responseData = JSON.parse(responseText);
            callback(responseData);//回调
        })
        .catch(error => {
            console.log(error);
            callback(post_error);
        });

}

export function NetWork_Get(net_api, callback, netOptions) {

    let url = reqUrl[net_api];
    let opt_error;

    if (typeof netOptions === 'object') {
        opt_error = netOptions['error'];
    };

    let get_error = opt_error ? opt_error : {
        status: '-1',
        msg: '请求失败',
    };

    fetch(url, {
        method: 'GET',
        credentials: 'include'
    })
        .then((response) => response.text())
        .then((responseText) => {
            let responseData = JSON.parse(responseText);
            callback(responseData);
        })
        .catch((error) => {
            callback(get_error);
        });
};
