// -- 商品转换编辑
import React, { Component } from 'react';
import { Form, Button, Input, message } from 'antd'
import { NetWork_Post } from '../../network/netUtils'
const FormItem = Form.Item;
class proFruitEdit extends Component {
    constructor(props) {
        super(props)
        this.state = {
            proFruitDetail: []
        }
    }
    componentDidMount() {
        //-- 获取商品转换详情
        this._getProFruitDetail()
        this.props.form.setFieldsValue({
            pro_code: this.props.match.params.pro_code,
        })
    }
    _getProFruitDetail = () => {
        const formData = {
            id: this.props.match.params.id
        }
        NetWork_Post('proFruitDetail', formData, (response) => {
            const { status, data, msg } = response
            console.log(data)
            if (status === '0000') {
                this.setState({
                    proFruitDetail: data[0]
                })
                this.props.form.setFieldsValue({
                    parent_code: data[0].parent_code,
                    pro_type_code: data[0].pro_type_code,
                    pro_type_name: data[0].pro_type_name,
                    remark: data[0].remark,
                    user_name: data[0].user_name
                })
            } else {
                if (status === '1003') return this.props.history.push('/');
                message.error(msg)
            }
        });
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (err) return;
            values.id = this.props.match.params.id
            NetWork_Post('proExcludeEdit', values, (response) => {
                const { status, msg } = response
                if (status === '0000') {
                    message.success(msg)
                    return this.props.history.goBack();
                } else {
                    if (status === '1003') return this.props.history.push('/');
                    message.error(msg)
                }
            });
        });
    }
    //-- 计算规格
    _checkTimes = () => {
        const { weight, number } = this.state;
        if (!weight || !number) {
            message.error('请输入正确的单果重量和数量')
        }
        const times = parseFloat(weight) * parseFloat(number)
        this.props.form.setFieldsValue({
            times: times
        })
    }
    render() {
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: { span: 12 },
                sm: { span: 3 },
            },
            wrapperCol: {
                xs: { span: 12 },
                sm: { span: 21 },
            },
        };
        return (
            <Form onSubmit={this.handleSubmit} style={{ width: '100%' }}>
                <FormItem
                    label="不推送商品编码:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pro_code', {
                        rules: [{
                            required: true, message: '请输入不推送商品编号！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem wrapperCol={{ span: 8, offset: 3 }}>
                    <div style={{ maxWidth: 300 }}>
                        <Button icon='save' htmlType="submit" style={{ float: "left" }}>
                            提交
                        </Button>
                        <Button onClick={() => { this.props.history.goBack() }} style={{ marginLeft: 10, float: "right" }} icon='rollback'>
                            返回
                        </Button>
                    </div>
                </FormItem>
            </Form>
        )
    }
}
export default Form.create()(proFruitEdit)
