// -- 添加大客户需求订单
import React, { Component } from 'react';
import { Form, Select, Button, Input, message, DatePicker, Radio, Table, Icon, Row, Col } from 'antd'
import { NetWork_Post } from '../../network/netUtils'
import { dateTransform } from '../../utils/utils_date';
const Option = Select.Option;
const { TextArea } = Input;
const FormItem = Form.Item;
const RadioGroup = Radio.Group;
class vendorAdd extends Component {
    constructor(props) {
        super(props)
        this.state = {
            productList: [],
            bindProductList: []
        }
    }
    componentDidMount() {
        this.props.form.setFieldsValue({
            order_num: 'Y' + dateTransform(new Date(), 'yyyyMMddhhmmss'),
            t_type: 1
        })
    }
    //-- 获取商品列表
    _getProductList = () => {
        const formData = {
            search_key: this.state.search_key
        }
        NetWork_Post('vendorProductList', formData, (response) => {
            const { status, data, msg } = response
            console.log('vendorProductList', response)
            if (status === '0000') {
                this.setState({
                    productList: data
                })
            } else {
                if (status === '1003') return this.props.history.push('/');
                message.error(msg)
            }
        });
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (err) return;
            console.log(values); return;
            NetWork_Post('vendorAdd', values, (response) => {
                const { status, msg } = response
                if (status === '0000') {
                    message.success(msg)
                    return this.props.history.goBack();
                } else {
                    if (status === '1003') return this.props.history.push('/');
                    message.error(msg)
                }
            });
        });
    }
    //-- 商品check按钮选择
    rowSelection = {
        onChange: (index, row) => {
            this.setState({
                selectedRowKeys: row
            })
        }
    }
    _onChangePicker = (date, dateString) => {
        console.log(date, dateString);
    }
    columns = [
        {
            align: 'center',
            title: '商品编号',
            dataIndex: 'product_num',
            key: 'product_num',
        }, {
            align: 'center',
