// -- 编辑厂商
import React, { Component } from 'react';
import { Form, Select, Button, Input, message } from 'antd'
import { NetWork_Post } from '../../network/netUtils'
const Option = Select.Option;
const { TextArea } = Input;
const FormItem = Form.Item;
class warehouseEdit extends Component {
    constructor(props) {
        super(props)
        this.state = {
            provinceIndex: null,
            cityIndex: null,
            areaIndex: null
        }
    }
    componentDidMount() {
        this._getWarehouseDetail()
        this._getProvinceList()
    }
    _getWarehouseDetail = () => {
        const formData = {
            id: this.props.match.params.id
        }
        NetWork_Post('warehouseDetail', formData, (response) => {
            console.log('warehouseDetail', response)
            const { status, data, msg } = response
            if (status === '0000') {
                this._getCityList({ area_name: data.province })
                this._getAreaList({ area_name: data.city })
                this.props.form.setFieldsValue({
                    warehouse_num: data.warehouse_num,
                    warehouse_name: data.warehouse_name,
                    pic_name: data.pic_name,
                    pic_tel: data.pic_tel,
                    pic_mobile: data.pic_mobile,
                    pic_fax: data.pic_fax,
                    pic_qq: data.pic_qq,
                    pic_wechat: data.pic_wechat,
                    pic_email: data.pic_email,
                    province: data.province,
                    city: data.city,
                    area: data.area,
                    address: data.address,
                    receiving_date: data.receiving_date,
                    remark: data.remark,
                })
            } else {
                if (status === '1003') return this.props.history.push('/');
                message.error(msg)
            }
        });
    }
    _getProvinceList = () => {
        const formData = {

        }
        NetWork_Post('areaList', formData, (response) => {
            console.log('ProvinceList', response)
            const { status, data } = response
            if (status === '0000') {
                this.setState({
                    provinceList: data
                })
            } else {
                if (status === '1003') return this.props.history.push('/');
            }
        });
    }
    _getCityList = ({ proviceIndex = '', area_name = '' }) => {
        const formData = {
            area_name: area_name ? area_name : this.state.provinceList[proviceIndex].area_name,
            area_level: 1
        }
        NetWork_Post('areaList', formData, (response) => {
            console.log('CityList', response)
            const { status, data } = response
            if (status === '0000') {
                this.setState(
                    {
                        cityList: data
                    },
                    () => area_name ? null : this.props.form.setFieldsValue({
                        city: null,
                        area: null
                    })
                )

            } else {
                if (status === '1003') return this.props.history.push('/');
            }
        });
    }
    _getAreaList = ({ cityIndex = '', area_name = '' }) => {
        const formData = {
            area_name: area_name ? area_name : this.state.cityList[cityIndex].area_name,
            area_level: 2
        }
        NetWork_Post('areaList', formData, (response) => {
            console.log('areaList', response)
            const { status, data } = response
            if (status === '0000') {
                this.setState(
                    {
                        areaList: data
                    },
                    () => this.props.form.setFieldsValue({
                        area: null
                    })
                )
            } else {
                if (status === '1003') return this.props.history.push('/');
            }
        });
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (err) return;
            values.id = this.props.match.params.id
            values.province = this.state.provinceList[values.province].area_name
            values.city = this.state.cityList[values.city].area_name
            values.area = this.state.areaList[values.area].area_name
            NetWork_Post('warehouseEdit', values, (response) => {
                const { status, msg } = response
                if (status === '0000') {
                    message.success(msg)
                    return this.props.history.goBack();
                } else {
                    if (status === '1003') return this.props.history.push('/');
                    message.error(msg)
                }
            });
        });
    }
    render() {
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: { span: 12 },
                sm: { span: 3 },
            },
            wrapperCol: {
                xs: { span: 12 },
                sm: { span: 21 },
            },
        };
        return (
            <Form onSubmit={this.handleSubmit} style={{ width: '100%' }}>
                <FormItem
                    label="仓库编号:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('warehouse_num', {
                        rules: [{
                            required: true, message: '请输入仓库编号！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="仓库名称:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('warehouse_name', {
                        rules: [{
                            required: true, message: '请输入仓库名称！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="负责人姓名:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pic_name', {
                        rules: [{
                            required: true, message: '请输入负责人姓名！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="负责人电话:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pic_tel', {
                        rules: [],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="负责人手机:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pic_mobile', {
                        rules: [{
                            required: true, message: '请输入负责人手机！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="负责人传真:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pic_fax', {
                        rules: [],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
