// -- 商品管理
import React, { Component } from 'react';
import { Table, Pagination, Button, Input, Icon, Divider, message } from 'antd'
import { Link } from 'react-router-dom'
import { NetWork_Post } from '../../network/netUtils'
export default class goodsCategory extends Component {
    constructor(props) {
        super(props)
        this.state = {
            data: [],
            page: 1,
            limit: 10,
            search_key: ''
        }
    }
    componentDidMount() {
        this._getGoodsCategoryList()
    }
    _getGoodsCategoryList = () => {
        const formData = {
            search_key: this.state.search_key,
            limit: this.state.limit,
            page: this.state.page
        }
        NetWork_Post('getGoodsCategoryList', formData, (response) => {
            const { status, data_list, message } = response
            if (status === '0000') {
                console.log(data_list)
                this.setState({
                    data: data_list.goodTypeList,
                    total: data_list.goodTypeCount
                })
            } else {
                if (status === '1003') return this.props.history.push('/');
                message.error(message)
            }
        });
    }
    columns = [
        {
            align: 'center',
            title: '商品类型编码',
            dataIndex: 'id',
            key: 'id',
        }, {
            align: 'center',
            title: '商品类型名称',
            dataIndex: 'pro_type_name',
            key: 'pro_type_name',
        }, {
            align: 'center',
            title: '父类型编码',
            dataIndex: 'parent_code',
            key: 'parent_code',
        }, {
            align: 'center',
            title: '采购人员',
            dataIndex: 'user_name',
            key: 'user_name',
        }, {
            align: 'center',
            title: '备注',
            dataIndex: 'remark',
            key: 'remark',
        }, {
            align: 'center',
            title: '创建人',
            dataIndex: 'create_by',
            key: 'create_by',
        }, {
            align: 'center',
            title: '创建时间',
            dataIndex: 'create_date',
            key: 'create_date',
        }, {
            align: 'center',
            title: '修改人',
            dataIndex: 'modify_by',
            key: 'modify_by',
        }, {
            align: 'center',
            title: '修改时间',
            dataIndex: 'modify_date',
            key: 'modify_date',
        }, {
            align: 'center',
            title: '采购人员用户名',
            key: 'action',
            render: (text, record) => (
                <span>
                    <Link to='bindSupplier'>
                        编辑
                    </Link>
                    <Divider type="vertical" />
                    <Link to='bindSupplier'>
                        添加子类
                    </Link>
                    <Divider type="vertical" />
                    <Link to='bindSupplier'>
                        删除
                    </Link>
                </span>
            ),
        }
    ];
    rowSelection = {
        onChange: (index, row) => { }
    }

    render() {
        return (
            <div style={{ width: '100%' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '100%', height: 50 }}>
                    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <Button icon="plus">新建</Button>
                        <Button style={{ margin: '0 10px' }} icon='delete'>批量删除</Button>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <Input value={this.state.search_key} prefix={<Icon type="search" style={{ color: '#b2b2b2' }} />} placeholder="搜索" onChange={(e) => this.setState({ search_key: e.target.value })} />
                        <Button style={{ margin: '0 10px' }} icon='search' onClick={() => { this._getGoodsCategoryList() }}>搜索</Button>
                    </div>
                </div>
                <Table
                    bordered
                    loading={false}
                    pagination={false} //-- 不使用自带的分页器
                    rowSelection={this.rowSelection}
                    dataSource={this.state.data}
                    columns={this.columns}
                    rowKey={(row) => row.id}
                    size='small'
                />
                <div style={{ width: '100%', display: 'flex', justifyContent: 'flex-end', alignItems: 'center', height: 60 }}>
                    <Pagination
                        showQuickJumper
                        showSizeChanger
                        defaultCurrent={1}
                        total={this.state.total}
                        onChange={(pageNumber) => { this.setState({ page: pageNumber }) }}
                        onShowSizeChange={onShowSizeChange}
                    />
                </div>
                <div style={{ width: '100%', borderTop: '1px solid #EFEFEF' }}>
                    <div style={{ margin: '10px 0' }}>
                        <span>请选择要导入的Excel: <Button size='small' icon='folder-open'>选取文件</Button></span>
                        <Divider type="vertical" />
                        <Button size='small'>商品信息导入</Button>
                        <Divider type="vertical" />
                        <Button size='small' icon='download'>商品信息导入模板下载</Button>
                    </div>
                </div>
            </div>
        )
    }
}
