const isInAction = (actionValue) => {
    //-- 获取权限列表
    var actionList = [];
    var aa = localStorage.getItem('ActionList');
    console.log(aa)
    return actionList.find((action, index, arr) => (action == actionValue));
}
export {
    isInAction
}
