// -- 商品管理
import React, { Component } from 'react';
import { Table, Pagination, Button, Input, Icon, Divider, message } from 'antd'
import { Link } from 'react-router-dom'
import { NetWork_File, NetWork_Post } from '../../network/netUtils'
export default class goods extends Component {
    constructor(props) {
        super(props)
        this.state = {
            data: [],
            page: 1,
            limit: 10,
            search_key: '',
            selectedRowKeys: [],
            fileList1: [],
            fileList2: [],
            uploading1: false,
            uploading2: false,
        }
    }

    componentDidMount() {
        //-- 获取商品列表
        this._getGoodsList()
        //-- 获取模版下载地址
        this._getProTypeDownload()
    }
    _getProTypeDownload = () => {
        const formData = {
        }
        NetWork_Post('proTypeDownload', formData, (response) => {
            const { status, data, msg } = response
            if (status === '0000') {
                this.setState({
                    proTypeDownload: data,
                })
            } else {
                if (status === '1003') return this.props.history.push('/');
                message.error(msg)
            }
        });
    }
    _getGoodsList = () => {
        const formData = {
            search_key: this.state.search_key,
            limit: this.state.limit,
            page: this.state.page
        }
        NetWork_Post('getGoodsList', formData, (response) => {
            console.log(response)
            const { status, data, msg } = response
            if (status === '0000') {
                this.setState({
                    data: data.productList,
                    total: data.productCount
                })
            } else {
                if (status === '1003') return this.props.history.push('/');
                message.error(msg)
            }
        });
    }
    rowSelection = {
        onChange: (index, row) => {
            this.setState({
                selectedRowKeys: index
            })
        }
    }
    onChangePage = (pageNumber) => {
        this.setState({
            page: pageNumber
        }, () => {
            this._getGoodsList()
        })
    }
    onChangeLimit = (page, limit) => {
        this.setState({
            page: page,
            limit: limit
        }, () => {
            this._getGoodsList()
        })
    }
    columns = [
        {
            align: 'center',
            title: '商品编号',
            dataIndex: 'product_num',
            key: 'product_num',
        }, {
            align: 'center',
            title: '商品名称',
            dataIndex: 'product_name',
            key: 'product_name',
        }, {
            align: 'center',
            title: '商品类型',
            dataIndex: 'product_type',
            key: 'product_type',
        }, {
            align: 'center',
            title: '进价',
            dataIndex: 'price',
            key: 'price',
        }, {
            align: 'center',
            title: '销售价',
            dataIndex: 'sale_price',
            key: 'sale_price',
        }, {
            align: 'center',
            title: '保质期',
            dataIndex: 'shelf_month',
            key: 'shelf_month',
        }, {
            align: 'center',
            title: '商品规格编码',
            dataIndex: 'specifications_num',
            key: 'specifications_num',
        }, {
            align: 'center',
            title: '计量单位',
            dataIndex: 'unit',
            key: 'unit',
        }, {
            align: 'center',
            title: '备注',
            dataIndex: 'remark',
            key: 'remark',
        }, {
            align: 'center',
            title: '修改人',
            dataIndex: 'modify_by',
            key: 'modify_by',
        }, {
            align: 'center',
            title: '修改时间',
            dataIndex: 'modify_date',
            key: 'modify_date',
        }, {
            align: 'center',
            title: '操作',
            key: 'action',
            render: (text, record) => (
                <span>
                    <Link to={'/goodsBindSupplier/' + text.id}>
                        绑定供应商
                    </Link>
                </span>
            ),
        }
    ];
    render() {
        return (
            <div style={{ width: '100%' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '100%', height: 50 }}>
                    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <Button icon="cloud-download">商品同步</Button>
                        <Button style={{ margin: '0 10px' }} icon="cloud-download">类别同步</Button>
                        <Button icon="cloud-download">供应商同步</Button>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <Input value={this.state.search_key} prefix={<Icon type="search" style={{ color: '#b2b2b2' }} />} placeholder="搜索" onChange={(e) => this.setState({ search_key: e.target.value })} />
                        <Button style={{ margin: '0 10px' }} icon='search' onClick={() => { this._getGoodsList() }}>搜索</Button>
                        <Button icon='file-done'>导出</Button>
                    </div>
                </div>
                <Table
                    bordered
                    loading={false}
                    pagination={false} //-- 不使用自带的分页器
                    rowSelection={this.rowSelection}
                    dataSource={this.state.data}
                    columns={this.columns}
                    rowKey={(row) => row.id}
                    size='small'
                />
                <div style={{ width: '100%', display: 'flex', justifyContent: 'flex-end', alignItems: 'center', height: 60 }}>
                    <Pagination
                        showQuickJumper
                        showSizeChanger
                        defaultCurrent={1}
                        total={this.state.total}
                        onChange={this.onChangePage}
                        onShowSizeChange={this.onChangeLimit}
                    />
                </div>
                <div style={{ width: '100%', borderTop: '1px solid #EFEFEF' }}>
                    <div style={{ margin: '10px 0' }}>
                        <span>请选择要导入的Excel: <Button size='small'>选取文件</Button></span>
                        <Divider type="vertical" />
                        <Button size='small'>商品信息导入</Button>
                        <Divider type="vertical" />
                        <Button size='small' icon="download">商品信息导入模板下载</Button>
                    </div>
                    <div>
                        <span>请选择要导入的Excel: <Button size='small'>选取文件</Button></span>
                        <Divider type="vertical" />
                        <Button size='small'>商品供应商关系信息导入</Button>
                        <Divider type="vertical" />
                        <Button size='small' icon="download">商品供应商关系信息模板下载</Button>
                    </div>
                </div>
            </div >
        )
    }
}
