// -- 绑定供应商
import React, { Component } from 'react';
import { Form, Select, Button, Input } from 'antd'
import { NetWork_Post } from '../../network/netUtils'
const Option = Select.Option;
const { TextArea } = Input;
const FormItem = Form.Item;
class goodsTypeAdd extends Component {
    constructor(props) {
        super(props)
        this.state = {
            goodTypeList: []
        }
    }
    componentDidMount() {
        this._getParentCodeList()
    }
    _getParentCodeList = () => {
        const formData = {
        }
        NetWork_Post('getParentCode', formData, (response) => {
            const { status, data_list, message } = response
            if (status === '0000') {
                this.setState({
                    goodTypeList: data_list.goodTypeList
                })
            } else {
                if (status === '1003') return this.props.history.push('/');
                message.error(message)
            }
        });
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (err) return;

        });
    }
    render() {
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: { span: 12 },
                sm: { span: 2 },
            },
            wrapperCol: {
                xs: { span: 12 },
                sm: { span: 22 },
            },
        };
        return (
            <Form onSubmit={this.handleSubmit} style={{ width: '100%' }}>
                <FormItem
                    {...formItemLayout}
                    label="父类型编号:"
                >
                    {getFieldDecorator('parent_code', {
                        rules: [{
                            required: true, message: '请选择父类型！',
                        }],
                    })(
                        <Select placeholder='请选择' style={{ maxWidth: 300 }}>
                            <Option value="0">代销</Option>
                            <Option value="1">经营</Option>
                            <Option value="2">联营</Option>
                        </Select>
                    )}
                </FormItem>
                <FormItem
                    label="产品类型编号:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pro_type_code', {
                        rules: [{
                            required: true, message: '请输入产品类型编号！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="产品类型名称:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('pro_type_name', {
                        rules: [{
                            required: true, message: '请输入产品类型名称！',
                        }],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem
                    label="产品类型备注:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('remark', {
                        rules: [],
                    })(
                        <TextArea style={{ maxWidth: 300 }} rows={4} />
                    )}
                </FormItem>
                <FormItem
                    label="采购人员姓名:"
                    {...formItemLayout}
                >
                    {getFieldDecorator('user_name', {
                        rules: [],
                    })(
                        <Input style={{ maxWidth: 300 }} />
                    )}
                </FormItem>
                <FormItem wrapperCol={{ span: 8, offset: 2 }}>
                    <div style={{ maxWidth: 300 }}>
                        <Button icon='save' htmlType="submit" style={{ float: "left" }}>
                            提交
                        </Button>
                        <Button onClick={() => { this.props.history.goBack() }} style={{ marginLeft: 10, float: "right" }} icon='rollback'>
                            返回
                        </Button>
                    </div>
                </FormItem>
            </Form>
        )
    }
}
const goodsTypeAdds = Form.create()(goodsTypeAdd)
export default goodsTypeAdds
